var ctypes = require('ctype')

ctypes.Int64 = ctypes.rsint64
ctypes.UInt64 = ctypes.ruint64
				
function isInteger(value) {
	/*if ((typeof value === "string" || value instanceof String) && /^\d+$/.test(value)) {
		return true;
	}*/
	return value instanceof ctypes.Int64 || value instanceof ctypes.UInt64 || Number.isInteger(value);
}

function equals(a, b) {
	if (Number.isInteger(a) && Number.isInteger(b)) {
		return a === b;
	}
	if (isInteger(a) && isInteger(b)) {
		return a.toString() === b.toString();
	}
	return undefined;
}


module.exports = {
	isInteger : isInteger,
	equals : equals
}
