var fnOptions = require('../bin/options.js');
var expect = require('expect.js');

describe('test options.js', function(){

    var argv = ['--test.format=html', '--test.pattern=.*Test.*,.*test.*' , '--coverage.reporting.format=html,cobertura'];
    var options = fnOptions(argv);

    console.log(options);

    it('parse cmd', function(){
        expect(options.test.format).to.eql('html');
        expect(options.test.pattern).to.eql(['.*Test.*', '.*test.*']);
        expect(options.coverage.reporting.format).to.eql(['html', 'cobertura']);
    });
});


