

function createReporter(options, jasmineInterface){
    var format = options.test.format

    if(!format) {
        var console = require('jasmine-core/lib/console/console.js')
        return new console.ConsoleReporter()
    }

    format = format.toLowerCase()

    var Reporter
    switch(format) {
        case 'json':
            Reporter = require('./jsonReporter.js')
            break
        case 'html':
            Reporter = require('./htmlReporter.js')
            break
        case 'junit':
        case 'xml':
            Reporter = require('./xmlReporter.js')
            break
        default:
            throw new Error('Unknown reporter format ' + format)
    }
    return new Reporter(options,jasmineInterface)
}

	
module.exports = {
    createReporter : createReporter
}
