var expect = require("expect.js");

var Error = require("../lib/error.js");

describe("error.js tests", function () {
    it("fills error objects with correct values", function () {
        var usageError = new Error.UsageError("a");
        expect(usageError.name).to.be("UsageError");
        expect(usageError.message).to.be("a");
        expect(usageError.stack).to.not.contain("UsageError@");

        var fatalError = new Error.FatalError("a");
        expect(fatalError.name).to.be("FatalError");
        expect(fatalError.message).to.be("a");
        expect(fatalError.stack).to.not.contain("FatalError@");

        var internalError = new Error.InternalError("a");
        expect(internalError.name).to.be("InternalError");
        expect(internalError.message).to.be("a");
        expect(internalError.stack).to.not.contain("InternalError@");
    });
});